import React, { useState } from 'react'

const BookingForm = () => {
    const [date, setDate] = useState("");
    const [times, setTimes] = useState("");
    const [guests, setGuests] = useState("");
    const [occasion, setOccasion] = useState("");

    const handleSubmit = (e) =>{
      e.preventDefault();
      props.SubmitForm(e);  
    }
    
    const handleChange = (e) =>{
        setDate(e);
        props.dispatch(e);
    }
  return (
    <header>
        <section>
            <form onSubmit={handleSubmit}>
                <fieldset>
                    {/* for date selection */}
                    <div>
                        <label htmlFor='book-date'>Choose Date</label>
                        <input id='book-date' value={date} onChange={(e) => handleChange(e.target.value)} type='date' required/>
                    </div>
                
                    {/* for time selection */}
                    <div>
                    <label htmlFor='book-time'>Choose Time:</label>
                    <select id='book-time' value={times} onChange={(e) => setTimes(e.target.value)}>
                        <option value="">Select a Time</option>
                        {
                            props.availableTimes.availableTimes.map(availableTimes => {return <option key=
                                {availableTimes}>{availableTimes}</option>})
                        }                            
                    </select>  
                    </div>

                    {/* for number of guests */}
                    <div>
                        <label htmlFor='book-guests'>Number of Guests:</label>
                        <input id='book-guests' min='1' value={guests} onChange={(e) => setGuests(e.target.value)}/>
                    </div>

                    {/* Ocassion field */}
                    <div>
                    <label htmlFor='book-ocassion'>Ocassion:</label>
                    <select id='book-ocassion' key={occasion} value={occasion} onChange={(e) => setOccasion(e.target.value)}>
                        <option>Birthday</option>
                        <option>Anniversary</option>
                    </select>
                    {/* Submit button */}
                    <div className='btnReceive'>
                        <input aria-label='OnClick' type='submit' value={"Make Your Resrvation"}/>
                    </div>
                    </div>
                </fieldset>
            </form>
        </section>
    </header>
  )
}

export default BookingForm;
